import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { ToastContainer, toast } from "react-toastify";

const AddEmployeeForm = () => {
  const [employeeName, setEmployeeName] = useState("");
  const [employeeRole, setEmployeeRole] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState(""); // For success or error messages
  const [loading, setLoading] = useState(false); // For loader
  const navigate = useNavigate(); // Initialize navigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Set loading to true when form is submitted

    try {
      // POST request to the backend
      const response = await axios.post("http://localhost:4000/user/add", {
        name: employeeName,
        role: employeeRole,
        email: email,
      });

      // Show success message and reset the form
      setMessage(response.data.message || "Employee added successfully!");
      setEmployeeName("");
      setEmployeeRole("");
      setEmail("");

      // Navigate to the admin dashboard after a short delay
      toast.success("Employee added successfully");
      setTimeout(() => {
        navigate("/admin-dashboard");
      }, 3000);
    } catch (error) {
      // Handle error and display error message
      console.error("There was an error adding the employee:", error);
      setMessage(error.response?.data?.error || "An error occurred.");
    } finally {
      setLoading(false); // Set loading to false once the process is done
    }
  };

  return (
    <div className="pl-8">
      <form
        onSubmit={handleSubmit}
        className="bg-whiter shadow-md rounded-lg p-6 w-96"
      >
        <div className="mb-4">
          <label
            htmlFor="employeeName"
            className="block text-mid font-medium mb-2"
          >
            Employee Name
          </label>
          <input
            type="text"
            id="employeeName"
            value={employeeName}
            onChange={(e) => setEmployeeName(e.target.value)}
            className="w-full px-4 py-2 border border-mid rounded focus:outline-none focus:ring-2 focus:ring-mid"
            placeholder="Enter employee name"
            required
          />
        </div>

        <div className="mb-4">
          <label
            htmlFor="employeeRole"
            className="block text-mid font-medium mb-2"
          >
            Role
          </label>
          <input
            type="text"
            id="employeeRole"
            value={employeeRole}
            onChange={(e) => setEmployeeRole(e.target.value)}
            className="w-full px-4 py-2 border border-mid rounded focus:outline-none focus:ring-2 focus:ring-mid"
            placeholder="Enter employee role"
            required
          />
        </div>

        <div className="mb-4">
          <label htmlFor="email" className="block text-mid font-medium mb-2">
            Email
          </label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-4 py-2 border border-mid rounded focus:outline-none focus:ring-2 focus:ring-mid"
            placeholder="Enter email"
            required
          />
        </div>

        <div className="flex justify-center">
          {loading ? (
            // Show processing button when the form is being submitted
            <button
              type="button"
              className="bg-indigo-500 text-white font-bold py-2 px-4 rounded inline-flex items-center"
              disabled
            >
              <svg
                className="animate-spin h-5 w-5 mr-3 text-white"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                ></circle>
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
                ></path>
              </svg>
              Processing...
            </button>
          ) : (
            // Show the regular submit button when not loading
            <button
              type="submit"
              className="w-full bg-dark hover:bg-darker text-whiter font-bold py-2 px-4 rounded transition duration-200"
            >
              Submit
            </button>
          )}
        </div>

        {message && (
          <div className="mt-4 text-center text-mid font-semibold">
            {message}
          </div>
        )}
      </form>
    </div>
  );
};

export default AddEmployeeForm;
