import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import CoursePieChart from "../charts/PieChart";
import BarChart from "../charts/BarChart";
import StatCard from "./StatCard";
import TopPerformersRadarChart from "../charts/TopPerformersRadarChart";
import TopPerformersTable from "../charts/TopPerformersTable";
import DoughnutChart from "../charts/DoughnutChart";
import Sidebar from "./Sidebar";

const AdminDashboard = () => {
  const [marks, setMarks] = useState([]);
  const [coursesCount, setCoursesCount] = useState(0);
  const [employeesCount, setEmployeesCount] = useState(0);
  const [trainersCount, setTrainersCount] = useState(0); // Assuming trainers are distinct based on some logic

  useEffect(() => {
    fetch("http://localhost:4000/marks")
      .then((response) => response.json())
      .then((data) => {
        setMarks(data);
        // Compute unique counts for courses and employees

        const uniqueCourses = new Set(data.map((mark) => mark.course_id)).size;
        const uniqueEmployees = new Set(data.map((mark) => mark.emp_id)).size;

        // Set the counts
        setCoursesCount(uniqueCourses);
        setEmployeesCount(uniqueEmployees);

        // Assuming trainers can be identified in the data or using a different logic
        // You can replace this with actual logic to compute trainers if needed
        const uniqueTrainers =
          new Set(data.map((mark) => mark.trainer_id)).size || 5;
        setTrainersCount(uniqueTrainers);
        console.log(trainersCount);
      })
      .catch((error) => console.error("Error fetching marks:", error));
  }, []);

  return (
    <div className="flex min-h-screen bg-light">
      {/* Sidebar */}
      <Sidebar />

      {/* Main content */}
      <main className="flex-1 p-6 bg-whiter">
        <h1 className="text-3xl font-bold text-dark mb-4">Welcome Admin</h1>
        {/* <p className="text-lg text-mid">
          Here you can manage users, view reports, and adjust settings.
        </p> */}

        {/* Stat Cards */}
        <div className="grid grid-cols-3 gap-5 mb-8">
          <StatCard title="Total Courses" count={coursesCount} bgColor="dark" />
          <StatCard
            title="Total Employees"
            count={employeesCount}
            bgColor="dark"
          />
          <StatCard
            title="Total Trainers"
            count={trainersCount}
            bgColor="dark"
          />
        </div>

        {/* Bar Chart */}
        <div className="grid grid-cols-2 gap-2">
          <div className="mt-5">
            {/* <h2 className="text-2xl font-bold mb-4">Employee Marks Overview</h2> */}
            <BarChart marks={marks} />
          </div>

          {/* Pie Chart */}
          <div className="mt-5">
            {/* <h2 className="text-2xl font-bold mb-4">
              Course Enrollment Distribution
            </h2> */}
            <CoursePieChart marks={marks} />
          </div>
          <div className="mt-5">
            <DoughnutChart marks={marks} />
          </div>
          <div className="mt-5">
            <TopPerformersTable data={marks} />
          </div>

          {/* Top Performers Radar Chart */}
          {/* <div className="mt-12 col-span-2">
            <h2 className="text-2xl font-bold mb-4">
              Top Performers Radar Chart
            </h2>
            <TopPerformersRadarChart data={marks} />
          </div> */}

          {/* Top Performers Table */}
          {/* <div className="mt-12 col-span-2">
            <h2 className="text-2xl font-bold mb-4">Top Performers Table</h2>
            <TopPerformersTable data={marks} />
          </div> */}
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
