import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const TrainerDashboard = () => {
  const { trainerId } = useParams();
  const [reqEmployees, setReqEmployees] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [marks, setMarks] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState(null);
  const [manualMarks, setManualMarks] = useState(0); // For manually entered marks
  const [modalData, setModalData] = useState({
    codingStandards: "",
    logicImplementation: "",
    documentation: "",
    teamwork: "",
    punctuality: "",
  });

  useEffect(() => {
    const trainerId = localStorage.getItem("id");

    const fetchEmployees = async () => {
      try {
        const response = await axios.get(
          `http://localhost:4000/trainer/fetchTrainers/${trainerId}`
        );
        setEmployees(response.data.employees);
      } catch (err) {
        setError("Failed to fetch employees.");
      } finally {
        setLoading(false);
      }
    };

    fetchEmployees();
  }, [trainerId]);

  useEffect(() => {
    const fetchAllEmployees = async () => {
      try {
        const response = await axios.get(
          "http://localhost:4000/trainer/fetchEmployees"
        );
        setReqEmployees(response.data);
      } catch (err) {
        console.error("Failed to fetch employee details:", err);
      }
    };

    fetchAllEmployees();
  }, []);

  const openModal = (employeeId) => {
    setSelectedEmployeeId(employeeId);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedEmployeeId(null);
    setManualMarks(0); // Reset manual marks
    setModalData({
      codingStandards: "",
      logicImplementation: "",
      documentation: "",
      teamwork: "",
      punctuality: "",
    });
  };

  const handleModalChange = (e) => {
    const { name, value } = e.target;
    setModalData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleManualMarksChange = (e) => {
    setManualMarks(Number(e.target.value));
  };

  const handleSubmitMarks = async () => {
    const calculateMarks = () => {
      let totalMarks = manualMarks; // Start with manually entered marks

      // Each field gives 5 marks for "Agree", 0 for "Disagree"
      const criteria = [
        "codingStandards",
        "logicImplementation",
        "documentation",
        "teamwork",
        "punctuality",
      ];
      criteria.forEach((criterion) => {
        if (modalData[criterion] === "agree") {
          totalMarks += 5;
        }
      });

      return totalMarks;
    };

    const totalMarks = calculateMarks();

    try {
      await axios.post(
        `http://localhost:4000/user/employees/${selectedEmployeeId}/addMarks`,
        {
          courseId: reqEmployees[0].course_id,
          totalMarks,
        }
      );
      alert("Marks added successfully!");

      setMarks((prevMarks) => ({
        ...prevMarks,
        [selectedEmployeeId]: { totalMarks },
      }));

      closeModal();
    } catch (err) {
      alert("Failed to add marks.");
    }
  };

  if (loading) {
    return <div className="text-light">Loading employees...</div>;
  }

  if (error) {
    return <div className="text-red-600">{error}</div>;
  }

  return (
    <div className="p-6 bg-whiter">
      <h1 className="text-dark text-2xl mb-4">
        Employees for Trainer ID: {localStorage.getItem("id")}
      </h1>

      <h2 className="text-xl mb-4">Trainer's Employees</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full max-w-3xl mx-auto border-collapse border border-darker rounded-lg shadow-lg overflow-hidden">
          <thead className="bg-dark text-whiter">
            <tr>
              <th className="border border-dark p-2 text-left">ID</th>
              <th className="border border-dark p-2 text-left">Name</th>
              <th className="border border-dark p-2 text-left">Email</th>
              <th className="border border-dark p-2 text-left">Course Name</th>
              <th className="border border-dark p-2 text-left">Mark 1</th>
              <th className="border border-dark p-2 text-left">Add Feedback</th>
              <th className="border border-dark p-2 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {reqEmployees.map((employee) => (
              <tr
                key={employee.id}
                className="hover:bg-mid transition duration-200"
              >
                <td className="border border-light p-2">{employee.id}</td>
                <td className="border border-light p-2">{employee.name}</td>
                <td className="border border-light p-2">{employee.email}</td>
                <td className="border border-light p-2">
                  {employee.course?.name}
                </td>
                <td className="border border-light p-2">
                  {marks[employee.id]?.totalMarks || "-"}
                </td>
                <td className="border border-light p-2">
                  <button
                    onClick={() => openModal(employee.id)}
                    className="bg-dark text-whiter p-2 rounded hover:bg-mid transition duration-200"
                  >
                    Add Marks
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-8 rounded shadow-lg max-w-lg w-full">
            <h2 className="text-xl mb-4">
              Add Marks for Employee {selectedEmployeeId}
            </h2>

            <form>
              <label className="block mb-4">
                Manual Marks Entry:
                <input
                  type="number"
                  value={manualMarks}
                  onChange={handleManualMarksChange}
                  className="border border-gray-400 p-2 w-full rounded"
                />
              </label>

              <label className="block mb-4">
                Coding Standards Followed:
                <select
                  name="codingStandards"
                  value={modalData.codingStandards}
                  onChange={handleModalChange}
                  className="border border-gray-400 p-2 w-full rounded"
                >
                  <option value="">Select</option>
                  <option value="agree">Agree (5 Marks)</option>
                  <option value="disagree">Disagree (0 Marks)</option>
                </select>
              </label>

              <label className="block mb-4">
                Logic Implementation:
                <select
                  name="logicImplementation"
                  value={modalData.logicImplementation}
                  onChange={handleModalChange}
                  className="border border-gray-400 p-2 w-full rounded"
                >
                  <option value="">Select</option>
                  <option value="agree">Agree (5 Marks)</option>
                  <option value="disagree">Disagree (0 Marks)</option>
                </select>
              </label>

              <label className="block mb-4">
                Documentation:
                <select
                  name="documentation"
                  value={modalData.documentation}
                  onChange={handleModalChange}
                  className="border border-gray-400 p-2 w-full rounded"
                >
                  <option value="">Select</option>
                  <option value="agree">Agree (5 Marks)</option>
                  <option value="disagree">Disagree (0 Marks)</option>
                </select>
              </label>

              <label className="block mb-4">
                Teamwork:
                <select
                  name="teamwork"
                  value={modalData.teamwork}
                  onChange={handleModalChange}
                  className="border border-gray-400 p-2 w-full rounded"
                >
                  <option value="">Select</option>
                  <option value="agree">Agree (5 Marks)</option>
                  <option value="disagree">Disagree (0 Marks)</option>
                </select>
              </label>

              <label className="block mb-4">
                Punctuality:
                <select
                  name="punctuality"
                  value={modalData.punctuality}
                  onChange={handleModalChange}
                  className="border border-gray-400 p-2 w-full rounded"
                >
                  <option value="">Select</option>
                  <option value="agree">Agree (5 Marks)</option>
                  <option value="disagree">Disagree (0 Marks)</option>
                </select>
              </label>

              <button
                type="button"
                onClick={handleSubmitMarks}
                className="bg-dark text-whiter p-2 rounded hover:bg-mid transition duration-200"
              >
                Submit
              </button>
              <button
                type="button"
                onClick={closeModal}
                className="bg-red-500 text-whiter p-2 ml-4 rounded hover:bg-red-600 transition duration-200"
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default TrainerDashboard;
